        public void InserirSenha(string campo, string numSmiles, string url, string senha)
        {
        	try {
        		Report.Log(ReportLevel.Info,"Inserting '" + senha + "'");
        		if (senha.Trim().Equals("ALEATORIO")) {
        			switch (UtilsDB.getDataValue(numSmiles, url, "SENHA")) {
        				case "1010":
        					senha = "1020";
        					_NovaSenha = senha;
        					break;
        				case "1020":
        					senha = "1010";
        					_NovaSenha = senha;
        					break;
        				default:
        					senha = "1010";
        					_NovaSenha = senha;
        					break;
        			}
        			InputTag i = repo.SmilesOMelhorProgramaDeMilhasS1.AbsoluteBasePath.ToResolvedString() + campo;
        			i.PressKeys(senha,Duration.FromMilliseconds(1));
        		}
        		else if (senha.Trim().Equals("CONFIRMAR")) {
        			senha = _NovaSenha;
        			InputTag i = repo.SmilesOMelhorProgramaDeMilhasS1.AbsoluteBasePath.ToResolvedString() + campo;
        			i.PressKeys(senha,Duration.FromMilliseconds(1));
        		}
        		else{
        			InputTag i = repo.SmilesOMelhorProgramaDeMilhasS1.AbsoluteBasePath.ToResolvedString() + campo;
        			_NovaSenha = senha;
        			i.PressKeys(senha,Duration.FromMilliseconds(1));
        		}
        		Report.Info(senha + " inserted");
        	} catch (Exception e) {
        		Report.Error("AlterarSenha - Error waiting browser loading");
        		throw new System.Exception("AlterarSenha.InserirSenha");
        	}
        }
    }
